    for (int i = 1; i < n; ++i) {
        if (protocol[i] == 0) { // IAmYourFriend
            friends[i].insert(host[i]);
            friends[host[i]].insert(i);
        } else if (protocol[i] == 1) { // MyFriendsAreYourFriends
            for (int friend_of_host : friends[host[i]]) {
                friends[i].insert(friend_of_host);
                friends[friend_of_host].insert(i);
            }
        } else if (protocol[i] == 2) { // WeAreYourFriends
            for (int friend_of_host : friends[host[i]]) {
                friends[i].insert(friend_of_host);
                friends[friend_of_host].insert(i);
            }
            friends[i].insert(host[i]);
            friends[host[i]].insert(i);
        }
    }

    // Maximum independent set using a greedy approach
    vector<bool> visited(n, false);
    vector<pair<int, int>> nodes; // Pair of (confidence, person)

    for (int i = 0; i < n; ++i) {
        nodes.emplace_back(confidence[i], i);
    }

    // Sort in descending order of confidence
    sort(nodes.rbegin(), nodes.rend());

    int totalConfidence = 0;

    for (auto& [conf, person] : nodes) {
