int findSample(int n, int confidence[], int host[], int protocol[]) {
    // Adjacency list to represent the friendship graph
    std::vector<std::unordered_set<int>> friends(n);

    // Build the network
    for (int i = 1; i < n; ++i) {
        int h = host[i];
        int p = protocol[i];

        if (p == 0) {
            // IAmYourFriend: Add friendship between i and host[h]
            friends[i].insert(h);
            friends[h].insert(i);
        } else if (p == 1) {
            // MyFriendsAreYourFriends: Add friendship between i and each friend of host[h]
            for (int friend_of_host : friends[h]) {
                friends[i].insert(friend_of_host);
                friends[friend_of_host].insert(i);
            }
        } else if (p == 2) {
            // WeAreYourFriends: Add friendship between i and host[h], and also between i and each friend of host[h]
            friends[i].insert(h);
            friends[h].insert(i);
            for (int friend_of_host : friends[h]) {
                friends[i].insert(friend_of_host);
                friends[friend_of_host].insert(i);
            }
        }
    }

    // Maximum independent set calculation using a greedy approach
    std::vector<std::pair<int, int>> people; // {confidence, person_id}
    for (int i = 0; i < n; ++i) {
        people.emplace_back(confidence[i], i);
    }

    // Sort people by decreasing confidence
    std::sort(people.rbegin(), people.rend());

    std::unordered_set<int> selected; // Selected people for the sample
    std::unordered_set<int> forbidden; // Forbidden people (friends of selected)

    for (const auto& [conf, person_id] : people) {
        if (forbidden.count(person_id) == 0) {
            // If person is not forbidden, add to the sample
            selected.insert(person_id);

            // Mark all friends of this person as forbidden
            for (int friend_id : friends[person_id]) {
                forbidden.insert(friend_id);
            }
        }
    }

    // Calculate total confidence of the selected sample
    int total_confidence = 0;
    for (int person_id : selected) {
        total_confidence += confidence[person_id];
    }

    return total_confidence;
}

