int find_best(int n) {
    int left = 0, right = n - 1;

    // Binary search to narrow down the box containing the diamond
    while (left <= right) {
        int mid = (left + right) / 2;
        vector<int> response = ask(mid);

        // If the diamond is in the current box
        if (response[0] == 0 && response[1] == 0) {
            return mid;
        }

        // If there are more expensive prizes to the left of the current box
        if (response[0] > 0) {
            right = mid - 1;
        } else { // If there are more expensive prizes to the right of the current box
            left = mid + 1;
        }
    }

    // This point should never be reached if the input guarantees one diamond
    return -1; 
}

