    // A helper function to determine if a box contains the diamond.
    auto is_diamond = [&](int i, const vector<int>& response) -> bool {
        // If the response is [0, 0], it means there are no more expensive prizes
        // to the left or right, so this box must contain the diamond.
        return response[0] == 0 && response[1] == 0;
    };

