// Helper function to check if all toys can be put away in `time` minutes
bool canPutAway(int time, int A, int B, int T, vector<int>& X, vector<int>& Y, vector<int>& W, vector<int>& S) {
    // Multisets to store the limits of robots
    multiset<int> weakRobots(X.begin(), X.end());
    multiset<int> smallRobots(Y.begin(), Y.end());

    // List of toys that need to be handled
    vector<pair<int, int>> toys; // {weight, size}
    for (int i = 0; i < T; ++i) {
        toys.emplace_back(W[i], S[i]);
    }

    // Sort toys so that we handle the hardest-to-place toys first
    sort(toys.begin(), toys.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
        return max(a.first, a.second) > max(b.first, b.second);
    });

    for (int i = 0; i < T;) {
        int handled = 0; // Number of toys handled in this iteration

        // Temporary copies of robot limits for this iteration
        multiset<int> tempWeakRobots = weakRobots;
        multiset<int> tempSmallRobots = smallRobots;

        for (int j = 0; j < time && i < T; ++j) {
            auto& [weight, size] = toys[i];

            // Try to use a weak robot
            auto itWeak = tempWeakRobots.lower_bound(weight + 1);
            bool usedWeak = false;
            if (itWeak != tempWeakRobots.end()) {
                tempWeakRobots.erase(itWeak);
                usedWeak = true;
            }

            // Try to use a small robot if a weak robot wasn't used
            if (!usedWeak) {
                auto itSmall = tempSmallRobots.lower_bound(size + 1);
                if (itSmall != tempSmallRobots.end()) {
                    tempSmallRobots.erase(itSmall);
                } else {
                    // If neither a weak nor a small robot can handle this toy, return false
                    return false;
                }
            }

            // If a robot was successfully used, mark the toy as handled
            ++handled;
            ++i;
        }

        // If no toys were handled in this iteration, it's impossible to proceed
        if (handled == 0) return false;
    }

    // If all toys have been handled, return true
    return true;
}

int putaway(int A, int B, int T, int X[], int Y[], int W[], int S[]) {
    // Convert input arrays to vectors for easier manipulation
    vector<int> weakRobots(X, X + A);
    vector<int> smallRobots(Y, Y + B);
    vector<int> toyWeights(W, W + T);
    vector<int> toySizes(S, S + T);

    // Check if any toy cannot be handled by any robot
    for (int i = 0; i < T; ++i) {
        bool canBeHandled = false;

        // Check if any weak robot can handle the toy
        for (int x : weakRobots) {
            if (toyWeights[i] < x) {
                canBeHandled = true;
                break;
            }
        }

        // Check if any small robot can handle the toy
        for (int y : smallRobots) {
            if (toySizes[i] < y) {
                canBeHandled = true;
                break;
            }
        }

        // If no robot can handle this toy, return -1
        if (!canBeHandled) return -1;
    }

    // Binary search for the minimum time
    int left = 1, right = T, result = T;
    while (left <= right) {
        int mid = left + (right - left) / 2;

        if (canPutAway(mid, A, B, T, weakRobots, smallRobots, toyWeights, toySizes)) {
            result = mid; // Update result and try for a smaller time
            right = mid - 1;
        } else {
            left = mid + 1; // Try for a larger time
        }
    }

    return result;
}

