// Helper function to check if all toys can be put away in 'minutes' time
bool canPutAwayInTime(int minutes, const vector<int>& weak_limits, const vector<int>& small_limits, const vector<pair<int, int>>& toys) {
    // Separate toys into weak and small categories
    vector<int> weak_toys, small_toys;
    for (const auto& toy : toys) {
        if (toy.first < weak_limits.front()) weak_toys.push_back(toy.second); // Toys valid for weak robots
        if (toy.second < small_limits.front()) small_toys.push_back(toy.first); // Toys valid for small robots
    }

    // Sort the toys in descending order for efficient allocation
    sort(weak_toys.begin(), weak_toys.end(), greater<int>());
    sort(small_toys.begin(), small_toys.end(), greater<int>());

    // Simulate toy allocation to robots
    auto allocateToys = [&](const vector<int>& robots, vector<int>& toys) -> bool {
        int robot_count = robots.size();
        for (int i = 0; i < minutes; ++i) {
            for (int j = 0; j < robot_count; ++j) {
                if (!toys.empty() && toys.back() < robots[j]) {
                    toys.pop_back(); // Assign toy to robot
                }
            }
        }
        return toys.empty(); // All toys successfully assigned
    };

    // Check if weak robots can handle weak toys
    if (!allocateToys(weak_limits, weak_toys)) return false;

    // Check if small robots can handle small toys
    if (!allocateToys(small_limits, small_toys)) return false;

    return true;
}

