        return a.first < b.first || (a.first == b.first && a.second < b.second);
    });

    // Check if there's any toy that cannot be handled by any robot
    for (const auto& toy : toys) {
        if (toy.first >= weak_limits.back() && toy.second >= small_limits.back()) {
            return -1; // Impossible to put away this toy
        }
    }

    // Perform binary search to find the minimum time
    int low = 1, high = T, result = -1;
    while (low <= high) {
        int mid = low + (high - low) / 2;
        if (canPutAwayInTime(mid, weak_limits, small_limits, toys)) {
            result = mid; // Update result
            high = mid - 1; // Try for a smaller time
        } else {
            low = mid + 1; // Try for a larger time
        }
    }

    return result;
}

