int64_t min_total_length(std::vector<int> r, std::vector<int> b) {
    int n = r.size();
    int m = b.size();
    int i = 0, j = 0; // Pointers for red and blue arrays
    int64_t total_length = 0;

    // Create arrays to store the minimum connection distances
    std::vector<int> min_red(n, INT32_MAX);
    std::vector<int> min_blue(m, INT32_MAX);

    // First pass: Calculate minimum distances from red to blue
    while (i < n && j < m) {
        if (r[i] < b[j]) {
            min_red[i] = std::min(min_red[i], std::abs(r[i] - b[j]));
            ++i;
        } else {
            min_blue[j] = std::min(min_blue[j], std::abs(b[j] - r[i]));
            ++j;
        }
    }

    // Second pass: Calculate minimum distances in the reverse direction
    i = n - 1;
    j = m - 1;
    while (i >= 0 && j >= 0) {
        if (r[i] > b[j]) {
            min_red[i] = std::min(min_red[i], std::abs(r[i] - b[j]));
            --i;
        } else {
            min_blue[j] = std::min(min_blue[j], std::abs(b[j] - r[i]));
            --j;
        }
    }

    // Compute the total length of wires
    for (int x = 0; x < n; ++x) {
        total_length += min_red[x];
    }
    for (int y = 0; y < m; ++y) {
        total_length += min_blue[y];
    }

    return total_length;
}

