    for (int i = 1; i < n; ++i) {
        for (int j = 0; j < i; ++j) {
            if (t[j] <= s[i]) { // Valid transition
                dp[i] = min(dp[i], dp[j] + max(0, s[i] - t[j]));
            }
        }
    }

    return *min_element(dp.begin(), dp.end());
}

