int64_t find_shortcut(int n, vector<int> l, vector<int> d, int c) {
    // Calculate prefix sums for the main line distances
    vector<int64_t> prefix_sum(n, 0);
    for (int i = 1; i < n; i++) {
        prefix_sum[i] = prefix_sum[i - 1] + l[i - 1];
    }

    // Compute distances from station 0 to all stations (left-to-right)
    vector<int64_t> dist_to_left(n, 0);
    for (int i = 0; i < n; i++) {
        dist_to_left[i] = prefix_sum[i];
    }

    // Compute distances from station n-1 to all stations (right-to-left)
    vector<int64_t> dist_to_right(n, 0);
    for (int i = 0; i < n; i++) {
        dist_to_right[i] = prefix_sum[n - 1] - prefix_sum[i];
    }

    // Compute max distances for stations considering secondary lines
    vector<int64_t> max_dist(n, 0);
    for (int i = 0; i < n; i++) {
        max_dist[i] = max(dist_to_left[i], dist_to_right[i]);
        if (d[i] > 0) {
            max_dist[i] = max(max_dist[i], d[i]);
        }
    }

    // Precompute the global maximum distance without any shortcut
    int64_t global_max = *max_element(max_dist.begin(), max_dist.end());

    // Try adding the express line between all pairs of stations (i, j)
    int64_t result = numeric_limits<int64_t>::max();
    int64_t min_left = numeric_limits<int64_t>::max();

    for (int i = 0, j = 0; i < n; i++) {
        // Update the minimum distance to the left
        min_left = min(min_left, dist_to_left[i] + d[i]);

        // Calculate the potential new diameter if we connect i and j
        int64_t candidate_diameter = max(global_max, max(dist_to_left[i], dist_to_right[j]) + c);
        result = min(result, candidate_diameter);
    }

    return result;
}

