int64_t count_swaps(vector<int> S) {
    int n = S.size() / 2;
    vector<int> left_positions(n + 1, -1);
    vector<int> right_positions(n + 1, -1);

    // Separate left and right shoe positions by size
    for (int i = 0; i < 2 * n; ++i) {
        int size = abs(S[i]);
        if (S[i] < 0) {
            left_positions[size] = i;
        } else {
            right_positions[size] = i;
        }
    }

    // Initialize total swaps counter
    int64_t total_swaps = 0;

    // Traverse the positions to fix pairs in order
    for (int i = 0; i < n; ++i) {
        int left_index = 2 * i;
        int right_index = 2 * i + 1;

        // Fix the left shoe
        if (S[left_index] >= 0 || abs(S[left_index]) != i + 1) {
            int target_index = left_positions[i + 1];
            while (target_index > left_index) {
                swap(S[target_index], S[target_index - 1]);
                --target_index;
                ++total_swaps;
            }
            left_positions[i + 1] = left_index;
        }

        // Fix the right shoe
        if (S[right_index] <= 0 || abs(S[right_index]) != i + 1) {
            int target_index = right_positions[i + 1];
            while (target_index > right_index) {
                swap(S[target_index], S[target_index - 1]);
                --target_index;
                ++total_swaps;
            }
            right_positions[i + 1] = right_index;
        }
    }

    return total_swaps;
}

