int64_t count_swaps(vector<int> S) {
    int n = S.size() / 2;
    vector<pair<int, int>> left_positions(n + 1, {-1, -1});
    vector<pair<int, int>> right_positions(n + 1, {-1, -1});
    vector<int> left_indices, right_indices;

    // Separate left and right shoes and store their positions
    for (int i = 0; i < S.size(); i++) {
        int size = abs(S[i]);
        if (S[i] < 0) { // Left shoe
            if (left_positions[size].first == -1) {
                left_positions[size].first = i;
            } else {
                left_positions[size].second = i;
            }
            left_indices.push_back(i);
        } else { // Right shoe
            if (right_positions[size].first == -1) {
                right_positions[size].first = i;
            } else {
                right_positions[size].second = i;
            }
            right_indices.push_back(i);
        }
    }

    // Calculate swaps to align left shoes
    int64_t swaps = 0;
    for (int i = 0; i < n; i++) {
        int target_pos = 2 * i; // Target position for left shoe
        int current_pos = left_indices[i];
        swaps += abs(current_pos - target_pos);

        // Update positions in the array
        for (int j = i + 1; j < n; j++) {
            if (left_indices[j] > current_pos) {
                left_indices[j]--;
            }
        }
    }

    // Calculate swaps to align right shoes
    for (int i = 0; i < n; i++) {
        int target_pos = 2 * i + 1; // Target position for right shoe
        int current_pos = right_indices[i];
        swaps += abs(current_pos - target_pos);

        // Update positions in the array
        for (int j = i + 1; j < n; j++) {
            if (right_indices[j] > current_pos) {
                right_indices[j]--;
            }
        }
    }

    return swaps;
}

