int style(int H, int W, int R[500][500], int G[500][500], int B[500][500]) {
    // Compute average values for R, G, B
    double avgR = 0, avgG = 0, avgB = 0;
    double varR = 0, varG = 0, varB = 0;
    int totalPixels = H * W;

    for (int i = 0; i < H; ++i) {
        for (int j = 0; j < W; ++j) {
            avgR += R[i][j];
            avgG += G[i][j];
            avgB += B[i][j];
        }
    }
    avgR /= totalPixels;
    avgG /= totalPixels;
    avgB /= totalPixels;

    // Compute variance for R, G, B
    for (int i = 0; i < H; ++i) {
        for (int j = 0; j < W; ++j) {
            varR += (R[i][j] - avgR) * (R[i][j] - avgR);
            varG += (G[i][j] - avgG) * (G[i][j] - avgG);
            varB += (B[i][j] - avgB) * (B[i][j] - avgB);
        }
    }
    varR /= totalPixels;
    varG /= totalPixels;
    varB /= totalPixels;

    // Analyze painting style based on features
    // Style 1: Neoplastic modern art - high variance in colors and balanced averages
    if (varR > 8000 && varG > 8000 && varB > 8000 && std::abs(avgR - avgG) < 30 && std::abs(avgG - avgB) < 30) {
        return 1;
    }

    // Style 2: Impressionist landscapes - high green average and low variance
    if (avgG > avgR && avgG > avgB && varR < 5000 && varG < 5000 && varB < 5000) {
        return 2;
    }

    // Style 3: Expressionist action paintings - high red average and high variance
    if (avgR > avgG && avgR > avgB && varR > 8000 && varG > 8000 && varB > 8000) {
        return 3;
    }

    // Style 4: Colour field paintings - low variance and average values close to each other
    if (varR < 3000 && varG < 3000 && varB < 3000 && std::abs(avgR - avgG) < 20 && std::abs(avgG - avgB) < 20) {
        return 4;
    }

    // Default case (fallback, should not happen ideally)
    return 1;
}

