// Helper function to compute the mean of a 2D array
double computeMean(int H, int W, int arr[500][500]) {
    long long sum = 0;
    for (int i = 0; i < H; ++i) {
        for (int j = 0; j < W; ++j) {
            sum += arr[i][j];
        }
    }
    return static_cast<double>(sum) / (H * W);
}

// Helper function to compute the standard deviation of a 2D array
double computeStdDev(int H, int W, int arr[500][500], double mean) {
    long long sumSquaredDiffs = 0;
    for (int i = 0; i < H; ++i) {
        for (int j = 0; j < W; ++j) {
            double diff = arr[i][j] - mean;
            sumSquaredDiffs += diff * diff;
        }
    }
    return sqrt(static_cast<double>(sumSquaredDiffs) / (H * W));
}

