int64_t minimum_walk(vector<int> p, int s) {
    int n = p.size();
    vector<bool> visited(n, false);
    int64_t total_distance = 0;

    for (int i = 0; i < n; ++i) {
        if (!visited[i]) {
            // Start a new cycle
            int cycle_start = i;
            int cycle_length = 0;
            int current_position = i;
            int64_t cycle_distance = 0;

            // Traverse the cycle
            while (!visited[current_position]) {
                visited[current_position] = true;
                int next_position = p[current_position];
                cycle_distance += abs(next_position - current_position);
                current_position = next_position;
                ++cycle_length;
            }

            // If the cycle contains more than 1 element, we need to account for walking
            if (cycle_length > 1) {
                // Add the cost of resolving the cycle
                total_distance += cycle_distance;

                // Add the cost of walking from s to the cycle and back
                total_distance += min(abs(cycle_start - s), abs(current_position - s));
            }
        }
    }

    return total_distance;
}

