            int current = i;
            int cycle_distance = 0;
            int cycle_start = current;
            int cycle_length = 0;

            // Follow the cycle
            do {
                visited[current] = true;
                int next = p[current];
                cycle_distance += std::abs(next - current);
                current = next;
                ++cycle_length;
            } while (current != cycle_start);

            // Add the distance to close the cycle (return to start of cycle)
            total_distance += cycle_distance;
        }
    }

    // Add the distance to return to the starting table
    total_distance += std::abs(s - s);
