#include <vector>
#include <cstdlib>
#include <cstdint>
using namespace std;

int64_t minimum_walk(vector<int> p, int s) {
    int n = p.size();
    vector<bool> visited(n, false);
    int64_t total_distance = 0;
    int current_pos = s;

    for (int i = 0; i < n; ++i) {
        if (!visited[i]) {
            // Start a new cycle
            vector<int> cycle;
            int x = i;
            while (!visited[x]) {
                visited[x] = true;
                cycle.push_back(x);
                x = p[x];
            }

            // If the cycle has length 1, skip it (no movement needed)
            if (cycle.size() == 1) continue;

            // Calculate the walking distance for this cycle
            int64_t cycle_distance = 0;

            // Start at the current position and walk to the first position in the cycle
            cycle_distance += abs(current_pos - cycle[0]);
            current_pos = cycle[0];

            // Walk through the cycle
            for (size_t j = 1; j < cycle.size(); ++j) {
                cycle_distance += abs(cycle[j] - current_pos);
                current_pos = cycle[j];
            }

            // Return to the starting point of the cycle
            cycle_distance += abs(cycle[0] - current_pos);
            current_pos = cycle[0];

            total_distance += cycle_distance;
        }
    }

    // Finally, return to the starting position s
    total_distance += abs(current_pos - s);

    return total_distance;
}

