// Function to implement the Hotter-Colder game
int HC(int N) {
    // Initialize search range
    int low = 1, high = N;

    // Make the first guess at the midpoint
    int mid = (low + high) / 2;
    int last_response = Guess(mid); // First call to Guess; response will be 0

    // Binary search with feedback
    while (low < high) {
        int next_mid = (low + high) / 2;

        // Make a guess and get the response
        int response = Guess(next_mid);

        if (response == 0) {
            // If response is "same", we've found the number
            return next_mid;
        } else if (response == 1) {
            // Hotter: the number is closer to the current guess
            if (next_mid > mid) {
                low = mid + 1; // Move the lower bound up
            } else {
                high = mid - 1; // Move the upper bound down
            }
        } else if (response == -1) {
            // Colder: the number is further from the current guess
            if (next_mid > mid) {
                high = mid - 1; // Move the upper bound down
            } else {
                low = mid + 1; // Move the lower bound up
            }
        }

        // Update the current midpoint
        mid = next_mid;
        last_response = response;
    }

    // When low == high, we've narrowed it down to the exact number
    return low;
}

