// Union-Find / Disjoint Set Union (DSU) for connectivity checks
class DSU {
public:
    vector<int> parent, rank;
    DSU(int n) {
        parent.resize(n);
        rank.resize(n, 0);
        for (int i = 0; i < n; i++) parent[i] = i;
    }
    int find(int x) {
        if (parent[x] != x) parent[x] = find(parent[x]);
        return parent[x];
    }
    void unite(int x, int y) {
        int xr = find(x), yr = find(y);
        if (xr == yr) return;
        if (rank[xr] < rank[yr]) {
            parent[xr] = yr;
        } else if (rank[xr] > rank[yr]) {
            parent[yr] = xr;
        } else {
            parent[yr] = xr;
            rank[xr]++;
        }
    }
};

// Function to implement
int construct_roads(vector<int> x, vector<int> y) {
    int n = x.size();
    DSU dsu(n);
    vector<pair<int, int>> fountains(n);
    unordered_map<int, vector<int>> x_map, y_map;

    // Map fountains by x and y coordinates
    for (int i = 0; i < n; i++) {
        fountains[i] = {x[i], y[i]};
        x_map[x[i]].push_back(i);
        y_map[y[i]].push_back(i);
    }

    // Connect fountains with valid roads using DSU
    vector<pair<int, int>> roads;
    for (int i = 0; i < n; i++) {
        // Check horizontal neighbors
        for (int j : x_map[x[i]]) {
            if (abs(y[j] - y[i]) == 2 && dsu.find(i) != dsu.find(j)) {
                dsu.unite(i, j);
                roads.push_back({i, j});
            }
        }
        // Check vertical neighbors
        for (int j : y_map[y[i]]) {
            if (abs(x[j] - x[i]) == 2 && dsu.find(i) != dsu.find(j)) {
                dsu.unite(i, j);
                roads.push_back({i, j});
            }
        }
    }

    // Check if all fountains are connected
    int root = dsu.find(0);
    for (int i = 1; i < n; i++) {
        if (dsu.find(i) != root) return 0; // Not all nodes are connected
    }

    // Assign benches
    vector<int> u, v, a, b;
    unordered_set<long long> benches; // To ensure unique benches
    for (auto& road : roads) {
        int i = road.first, j = road.second;
        int bench_x = (x[i] + x[j]) / 2 + 1; // Pick an odd coordinate
        int bench_y = (y[i] + y[j]) / 2 + 1; // Pick an odd coordinate
        while (benches.count((long long)bench_x * 200001 + bench_y)) {
            bench_x += 2; bench_y += 2; // Ensure uniqueness
        }
        benches.insert((long long)bench_x * 200001 + bench_y);
