// Helper function to calculate Manhattan distance between two points
int manhattan_distance(int x1, int y1, int x2, int y2) {
    return abs(x1 - x2) + abs(y1 - y2);
}

// Disjoint Set Union (DSU) for Kruskal's MST
class DSU {
public:
    vector<int> parent, rank;

    DSU(int n) {
        parent.resize(n);
        rank.resize(n, 0);
        for (int i = 0; i < n; ++i) parent[i] = i;
    }

    int find(int u) {
        if (parent[u] != u) parent[u] = find(parent[u]);
        return parent[u];
    }

    bool unite(int u, int v) {
        int root_u = find(u);
        int root_v = find(v);
        if (root_u == root_v) return false;
        if (rank[root_u] < rank[root_v]) {
            parent[root_u] = root_v;
        } else if (rank[root_u] > rank[root_v]) {
            parent[root_v] = root_u;
        } else {
            parent[root_v] = root_u;
            rank[root_u]++;
        }
        return true;
    }
};

// Function to construct roads and benches
int construct_roads(vector<int> x, vector<int> y) {
    int n = x.size();
    vector<tuple<int, int, int>> edges; // (distance, node1, node2)

    // Generate all possible edges (Manhattan distance between fountains)
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            if (x[i] == x[j] || y[i] == y[j]) { // Valid road (horizontal or vertical)
                int dist = manhattan_distance(x[i], y[i], x[j], y[j]);
                edges.emplace_back(dist, i, j);
            }
        }
    }

    // Sort edges by distance (for Kruskal's MST)
    sort(edges.begin(), edges.end());

    // Kruskal's MST algorithm
    DSU dsu(n);
    vector<int> u, v, a, b; // Roads and benches
    for (const auto& [dist, node1, node2] : edges) {
        if (dsu.unite(node1, node2)) {
            // Add road
            u.push_back(node1);
            v.push_back(node2);

            // Calculate bench location
            int mid_x = (x[node1] + x[node2]) / 2;
            int mid_y = (y[node1] + y[node2]) / 2;

            // Offset midpoint to find a valid bench location
            a.push_back(mid_x + 1); // Odd x-coordinate
            b.push_back(mid_y + 1); // Odd y-coordinate
        }
    }

    // Check if the graph is connected
    int root = dsu.find(0);
    for (int i = 1; i < n; ++i) {
        if (dsu.find(i) != root) return 0; // Not connected
    }

    // Call build function with the constructed roads and benches
    build(u, v, a, b);
    return 1;
}

