    }

    vector<int> u, v, a, b; // Arrays for roads and benches.
    UnionFind uf(n);

    unordered_set<string> bench_positions; // To ensure distinct bench locations.

    // Iterate over all pairs of fountains to construct roads.
    for (int i = 0; i < n; ++i) {
        for (int j = i + 1; j < n; ++j) {
            int x1 = fountains[i].first, y1 = fountains[i].second;
            int x2 = fountains[j].first, y2 = fountains[j].second;

            // Check if fountains are exactly two units apart horizontally or vertically.
