using namespace std;

const int MOD = 1000000000;

void DistanceSum(int N, vector<int> X, vector<int> Y, long long &result) {
    // Sort the X and Y coordinates
    sort(X.begin(), X.end());
    sort(Y.begin(), Y.end());

    // Helper function to compute 1D contribution
    auto compute_1D_contribution = [](const vector<int> &coords) -> long long {
        long long sum = 0, prefix_sum = 0;
        int n = coords.size();
        for (int i = 0; i < n; ++i) {
            long long current = coords[i];
            sum = (sum + (current * i - prefix_sum) % MOD) % MOD;
            prefix_sum = (prefix_sum + current) % MOD;
        }
        return sum;
    };

    // Compute contributions for X and Y
    long long x_contribution = compute_1D_contribution(X);
    long long y_contribution = compute_1D_contribution(Y);

    // Combine results and take modulo
    result = (x_contribution + y_contribution) % MOD;
    if (result < 0) result += MOD;
}

