using namespace std;

const int MOD = 1'000'000'000;

long long computeCoordinateContribution(vector<int>& coords) {
    int n = coords.size();
    sort(coords.begin(), coords.end());

    vector<long long> prefixSum(n, 0);
    prefixSum[0] = coords[0];
    for (int i = 1; i < n; ++i) {
        prefixSum[i] = (prefixSum[i - 1] + coords[i]) % MOD;
    }

    long long contribution = 0;
    for (int i = 0; i < n; ++i) {
        long long leftSum = i > 0 ? prefixSum[i - 1] : 0;
        long long rightSum = (prefixSum[n - 1] - prefixSum[i] + MOD) % MOD;
        long long leftCount = i;
        long long rightCount = n - i - 1;

        long long currentContribution = (leftCount * 1LL * coords[i] - leftSum + MOD) % MOD;
        currentContribution = (currentContribution + (rightSum - rightCount * 1LL * coords[i] + MOD) % MOD) % MOD;
        contribution = (contribution + currentContribution) % MOD;
    }

    return contribution;
}

int DistanceSum(int N, vector<int> X, vector<int> Y) {
    long long xContribution = computeCoordinateContribution(X);
    long long yContribution = computeCoordinateContribution(Y);
    return (xContribution + yContribution) % MOD;
}

