const int MOD = 1000000000;

long long calculateSum(vector<int>& coords, int N) {
    // Sort the coordinates
    sort(coords.begin(), coords.end());

    // Prefix sum array
    vector<long long> prefixSum(N, 0);
    prefixSum[0] = coords[0];
    for (int i = 1; i < N; i++) {
        prefixSum[i] = (prefixSum[i - 1] + coords[i]) % MOD;
    }

    // Calculate the total sum of pairwise distances
    long long totalSum = 0;
    for (int i = 0; i < N; i++) {
        long long leftSum = (1LL * coords[i] * i - (i > 0 ? prefixSum[i - 1] : 0)) % MOD;
        long long rightSum = ((prefixSum[N - 1] - prefixSum[i] + MOD) % MOD - 1LL * coords[i] * (N - i - 1)) % MOD;
        totalSum = (totalSum + leftSum + rightSum) % MOD;
    }

    return totalSum;
}

int DistanceSum(int N, vector<int> X, vector<int> Y) {
    // Calculate the sum of pairwise distances for X and Y separately
    long long xSum = calculateSum(X, N);
    long long ySum = calculateSum(Y, N);

    // Combine the results and return modulo MOD
    return (xSum + ySum) % MOD;
}

