const int MOD = 1000000000;

long long computeSum(const vector<int>& coords) {
    int N = coords.size();
    vector<int> sorted_coords = coords;
    sort(sorted_coords.begin(), sorted_coords.end());

    long long prefix_sum = 0;
    long long total_sum = 0;

    for (int i = 0; i < N; ++i) {
        long long current = sorted_coords[i];
        // Contribution of sorted_coords[i]
        total_sum += (current * i - prefix_sum) % MOD;
        total_sum %= MOD;
        prefix_sum += current;
        prefix_sum %= MOD;
    }

    return total_sum;
}

int DistanceSum(int N, vector<int> X, vector<int> Y) {
    // Compute the sum of pairwise distances for X-coordinates and Y-coordinates
    long long sum_x = computeSum(X);
    long long sum_y = computeSum(Y);

    // Total sum is the sum of both contributions, modulo MOD
    return (sum_x + sum_y) % MOD;
}

