// Preprocess the height array for range maximum queries using a sparse table
void buildSparseTable() {
    int logN = log2(N) + 1;
    sparseTable.assign(N, vector<int>(logN));
    for (int i = 0; i < N; ++i) {
        sparseTable[i][0] = heights[i];
    }
    for (int j = 1; (1 << j) <= N; ++j) {
        for (int i = 0; i + (1 << j) - 1 < N; ++i) {
            sparseTable[i][j] = max(sparseTable[i][j - 1], sparseTable[i + (1 << (j - 1))][j - 1]);
        }
    }
}

// Query the maximum value in the range [L, R]
int rangeMax(int L, int R) {
    int j = log2(R - L + 1);
    return max(sparseTable[L][j], sparseTable[R - (1 << j) + 1][j]);
}

