    }
    sort(rangeTowers.begin(), rangeTowers.end());

    // DP to find the largest valid subset
    int maxTowers = 0;
    vector<int> dp(rangeTowers.size(), 1);

    for (int i = 0; i < rangeTowers.size(); ++i) {
        int h1 = rangeTowers[i].first;
        int idx1 = rangeTowers[i].second;
        for (int j = 0; j < i; ++j) {
            int h2 = rangeTowers[j].first;
            int idx2 = rangeTowers[j].second;
            if (idx2 < idx1) {
                int kMax = rangeMax(idx2 + 1, idx1 - 1);
                if (h1 <= kMax - D && h2 <= kMax - D) {
                    dp[i] = max(dp[i], dp[j] + 1);
                }
            }
        }
        maxTowers = max(maxTowers, dp[i]);
    }

    return maxTowers;
}

