void dfs(int current, int parent, int current_length, int current_highways, int K, const vector<vector<pair<int, int>>>& adj, int& min_highways) {
    // If the current length exceeds K, stop exploring this path
    if (current_length > K) return;

    // If the current length is exactly K, update the minimum number of highways
    if (current_length == K) {
        min_highways = min(min_highways, current_highways);
        return;
    }

    // Explore neighbors
    for (const auto& neighbor : adj[current]) {
        int next_city = neighbor.first;
        int highway_length = neighbor.second;

        // Avoid revisiting the parent city
        if (next_city != parent) {
            dfs(next_city, current, current_length + highway_length, current_highways + 1, K, adj, min_highways);
        }
    }
}

