    for (int i = 0; i < H.size(); ++i) {
        int u = H[i][0];
        int v = H[i][1];
        int length = L[i];
        adj[u].emplace_back(v, length);
        adj[v].emplace_back(u, length);
    }

    // Step 2: Initialize the result
    int min_highways = N + 1; // Initialize with a large value

    // Step 3: Perform DFS from each city
    for (int start_city = 0; start_city < N; ++start_city) {
        dfs(start_city, -1, 0, 0, K, adj, min_highways);
    }

    // Step 4: Return the result
    return (min_highways == N + 1) ? -1 : min_highways;
}

