            for (int col = 1; col < m - 1; ++col) {
                if (is_valid[r2][col]) {
                    continuous_valid[col]++;
                } else {
                    continuous_valid[col] = 0; // Reset if not valid
                }
            }

            // Count rectangles in the current row range [r1, r2]
            int consecutive = 0;
            for (int col = 1; col < m - 1; ++col) {
                if (continuous_valid[col] == (r2 - r1 + 1)) {
                    ++consecutive;
                } else {
                    total_rectangles += (int64_t)consecutive * (consecutive + 1) / 2;
                    consecutive = 0;
                }
            }
            total_rectangles += (int64_t)consecutive * (consecutive + 1) / 2;
        }
    }

    return total_rectangles;
}

