int64_t count_rectangles(vector<vector<int>> a) {
    int64_t count = 0;
    int n = a.size();
    int m = a[0].size();

    // Precompute the max heights for adjacent columns
    vector<vector<int>> left(n, vector<int>(m, 0));
    vector<vector<int>> right(n, vector<int>(m, 0));
    for (int i = 0; i < n; ++i) {
        left[i][0] = a[i][0];
        for (int j = 1; j < m; ++j) {
            left[i][j] = max(left[i][j - 1], a[i][j]);
        }
        right[i][m - 1] = a[i][m - 1];
        for (int j = m - 2; j >= 0; --j) {
            right[i][j] = max(right[i][j + 1], a[i][j]);
        }
    }

    // Precompute the max heights for adjacent rows
    vector<vector<int>> up(n, vector<int>(m, 0));
    vector<vector<int>> down(n, vector<int>(m, 0));
    for (int j = 0; j < m; ++j) {
        up[0][j] = a[0][j];
        for (int i = 1; i < n; ++i) {
            up[i][j] = max(up[i - 1][j], a[i][j]);
        }
        down[n - 1][j] = a[n - 1][j];
        for (int i = n - 2; i >= 0; --i) {
            down[i][j] = max(down[i + 1][j], a[i][j]);
        }
    }

    // Iterate over all possible rectangles
    for (int r1 = 1; r1 < n - 1; ++r1) {
        for (int r2 = r1; r2 < n - 1; ++r2) {
            vector<int> valid_start(m, 1); // Tracks valid columns for the current rectangle
            for (int c = 1; c < m - 1; ++c) {
                for (int i = r1; i <= r2; ++i) {
                    if (a[i][c] >= left[i][c - 1] || a[i][c] >= right[i][c + 1] ||
                        a[i][c] >= up[r1 - 1][c] || a[i][c] >= down[r2 + 1][c]) {
                        valid_start[c] = 0;
                        break;
                    }
                }
            }

            // Count valid rectangles for the current row range
            int c1 = 1;
            while (c1 < m - 1) {
                if (valid_start[c1]) {
                    int c2 = c1;
                    while (c2 < m - 1 && valid_start[c2]) {
                        ++c2;
                    }
                    int width = c2 - c1;
                    count += width;
                    c1 = c2; // Move to the next invalid column
                } else {
                    ++c1;
                }
            }
        }
    }

    return count;
}

