int findSwapPairs(int N, int S[], int M, int X[], int Y[], int P[], int Q[]) {
    // Convert S[] into a vector for easier manipulation
    std::vector<int> sequence(S, S + N);
    std::vector<int> sorted_sequence = sequence;
    std::sort(sorted_sequence.begin(), sorted_sequence.end()); // Target sorted sequence

    int rounds = 0; // Count of swaps Aizhan makes

    for (int i = 0; i < M; i++) {
        // Ermek's swap
        std::swap(sequence[X[i]], sequence[Y[i]]);

        // Check if the sequence is already sorted
        if (sequence == sorted_sequence) {
            // Aizhan can swap two identical indices to terminate
            P[rounds] = 0;
            Q[rounds] = 0;
            rounds++;
            break;
        }

        // Aizhan's turn: Find a swap to bring the sequence closer to being sorted
        for (int j = 0; j < N; j++) {
            if (sequence[j] != sorted_sequence[j]) {
                // Find the correct position for sequence[j]
                int target_index = std::find(sequence.begin(), sequence.end(), sorted_sequence[j]) - sequence.begin();

                // Perform the swap
                std::swap(sequence[j], sequence[target_index]);

                // Record the swap
                P[rounds] = j;
                Q[rounds] = target_index;
                rounds++;
                break;
            }
        }

        // Check again if the sequence is sorted after Aizhan's swap
        if (sequence == sorted_sequence) {
            break;
        }
    }

    return rounds;
}

