using namespace std;

int findSwapPairs(int N, int S[], int M, int X[], int Y[], int P[], int Q[]) {
    // Copy the input array S to work on it
    vector<int> sequence(S, S + N);
    vector<pair<int, int>> swaps; // To store Aizhan's swaps

    // Create a sorted version of the sequence to compare against
    vector<int> sortedSequence = sequence;
    sort(sortedSequence.begin(), sortedSequence.end());

    // A helper function to perform a swap on the sequence
    auto performSwap = [&](int i, int j) {
        if (i != j) {
            swap(sequence[i], sequence[j]);
        }
    };

    // Process each round
    for (int i = 0; i < M; ++i) {
        // Ermek's swap
        performSwap(X[i], Y[i]);

        // Check if the array is already sorted
        if (sequence == sortedSequence) {
            // If sorted, we can stop and fill in dummy swaps for remaining rounds
            for (int j = swaps.size(); j < M; ++j) {
                swaps.emplace_back(0, 0); // Dummy swaps
            }
            break;
        }

        // Aizhan's turn: Find a swap to help sort the sequence
        // Use a greedy approach to put elements in their correct position
        for (int j = 0; j < N; ++j) {
            if (sequence[j] != sortedSequence[j]) {
                // Element at position j is not in its correct position
                // Find where the correct element for position j is located
                int targetIndex = find(sequence.begin(), sequence.end(), sortedSequence[j]) - sequence.begin();
                // Perform the swap
                performSwap(j, targetIndex);
                swaps.emplace_back(j, targetIndex);
                break;
            }
        }
    }

    // Copy the swaps into P and Q
    for (int i = 0; i < swaps.size(); ++i) {
        P[i] = swaps[i].first;
        Q[i] = swaps[i].second;
    }

    // Return the number of swaps performed by Aizhan
    return swaps.size();
}

