int findSwapPairs(int N, int S[], int M, int X[], int Y[], int P[], int Q[]) {
    // Step 1: Convert S to a vector and create a sorted version
    vector<int> sequence(S, S + N);
    vector<int> sorted_sequence = sequence;
    sort(sorted_sequence.begin(), sorted_sequence.end());

    // Step 2: Create a map to track the current positions of elements
    unordered_map<int, int> position;
    for (int i = 0; i < N; ++i) {
        position[sequence[i]] = i;
    }

    int rounds = 0; // Count of Aizhan's swaps
    for (int i = 0; i < M; ++i) {
        // Step 3: Simulate Ermek's swap
        int ermek_x = X[i];
        int ermek_y = Y[i];
        swap(sequence[ermek_x], sequence[ermek_y]);
        position[sequence[ermek_x]] = ermek_x;
        position[sequence[ermek_y]] = ermek_y;

        // Step 4: Check if the sequence is already sorted
        bool sorted = true;
        for (int j = 0; j < N - 1; ++j) {
            if (sequence[j] > sequence[j + 1]) {
                sorted = false;
                break;
            }
        }
        if (sorted) break;

        // Step 5: Determine Aizhan's optimal swap
        for (int j = 0; j < N; ++j) {
            int target_value = sorted_sequence[j];
            int current_value = sequence[j];

            if (current_value != target_value) {
                int target_position = position[target_value];
                // Perform the swap
                swap(sequence[j], sequence[target_position]);
                position[current_value] = target_position;
                position[target_value] = j;

                // Record the swap
                P[rounds] = j;
                Q[rounds] = target_position;
                ++rounds;

                break; // One swap per round
            }
        }
    }

    return rounds;
}

