int findSwapPairs(int N, int S[], int M, int X[], int Y[], int P[], int Q[]) {
    // Convert S to a vector for easier manipulation
    std::vector<int> sequence(S, S + N);

    // Create a sorted version of the sequence for reference
    std::vector<int> sorted_sequence = sequence;
    std::sort(sorted_sequence.begin(), sorted_sequence.end());

    // Track the swaps performed by Aizhan
    int R = 0;

    for (int i = 0; i < M; ++i) {
        // Perform Ermek's swap
        std::swap(sequence[X[i]], sequence[Y[i]]);

        // Check if the sequence is sorted
        if (sequence == sorted_sequence) {
            // If sorted, Aizhan can perform a "dummy" swap
            P[R] = 0;
            Q[R] = 0;
            ++R;
            break;
        }

        // Find the first misplaced element and its correct position
        int misplaced_index = -1;
        for (int j = 0; j < N; ++j) {
            if (sequence[j] != sorted_sequence[j]) {
                misplaced_index = j;
                break;
            }
        }

        // If everything is sorted, stop
        if (misplaced_index == -1) break;

        // Find the correct position of the misplaced element
        int correct_value = sorted_sequence[misplaced_index];
        int correct_index = -1;
        for (int j = 0; j < N; ++j) {
            if (sequence[j] == correct_value) {
                correct_index = j;
                break;
            }
        }

        // Perform Aizhan's swap
        P[R] = misplaced_index;
        Q[R] = correct_index;
        ++R;
        std::swap(sequence[misplaced_index], sequence[correct_index]);
    }

    return R;
}

