// Function to find the diameter of a connected component
int bfsDiameter(const vector<vector<pair<int, int>>> &graph, int start) {
    int n = graph.size();
    vector<int> dist(n, INF);
    queue<int> q;

    dist[start] = 0;
    q.push(start);

    int farthestNode = start;
    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (const auto &edge : graph[u]) {
            int v = edge.first, w = edge.second;
            if (dist[v] == INF) {
                dist[v] = dist[u] + w;
                q.push(v);
                if (dist[v] > dist[farthestNode]) {
                    farthestNode = v;
                }
            }
        }
    }

    // Second BFS to find the diameter
    dist.assign(n, INF);
    dist[farthestNode] = 0;
    q.push(farthestNode);

    int diameter = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();

        for (const auto &edge : graph[u]) {
            int v = edge.first, w = edge.second;
            if (dist[v] == INF) {
                dist[v] = dist[u] + w;
                q.push(v);
                diameter = max(diameter, dist[v]);
            }
        }
    }

    return diameter;
}

