        vector<int> dist(N, numeric_limits<int>::max());
        queue<int> q;
        q.push(start);
        dist[start] = 0;
        visited[start] = true;

        int farthestNode = start;
        while (!q.empty()) {
            int node = q.front();
            q.pop();

            for (auto& [neighbor, weight] : adj[node]) {
                if (!visited[neighbor]) {
                    visited[neighbor] = true;
                    dist[neighbor] = dist[node] + weight;
                    q.push(neighbor);
                    if (dist[neighbor] > dist[farthestNode]) {
                        farthestNode = neighbor;
                    }
                }
            }
        }
        return make_pair(farthestNode, dist[farthestNode]);
    };

    for (int i = 0; i < N; i++) {
        if (!visited[i]) {
            // Find one end of the diameter
            auto [farthestNode1, _] = bfs(i);

            // Find the other end of the diameter
            fill(visited.begin(), visited.end(), false);
            auto [farthestNode2, diameter] = bfs(farthestNode1);

            componentDiameters.push_back(diameter);
            farthestPoints.push_back(farthestNode1);
        }
    }

    // Step 3: Calculate the result by connecting components
    int numComponents = componentDiameters.size();
    if (numComponents == 1) {
        return componentDiameters[0]; // If everything is already connected
    }

    sort(componentDiameters.begin(), componentDiameters.end(), greater<int>());

    // If we connect the two largest components with a new trail of length L
    int maxDiameter = max(componentDiameters[0], componentDiameters[1] + L);

    // If there are more than two components, consider connecting the others
    for (int i = 2; i < numComponents; i++) {
        maxDiameter = max(maxDiameter, componentDiameters[i] + 2 * L);
    }

    return maxDiameter;
}

