    int max_distance = 0;

    while (!q.empty()) {
        int current = q.front();
        q.pop();

        for (const auto& neighbor : graph[current]) {
            int next = neighbor.first;
            int weight = neighbor.second;

            if (dist[next] == -1) { // Not visited
                dist[next] = dist[current] + weight;
                q.push(next);

                if (dist[next] > max_distance) {
                    max_distance = dist[next];
                    farthest_node = next;
                }
            }
        }
    }

    return {farthest_node, max_distance};
}

// Function to calculate the diameter of a connected component
int calculateDiameter(const vector<vector<pair<int, int>>>& graph, int start) {
    auto [farthest_node, _] = bfs(start, graph); // First BFS to find one endpoint of the diameter
    auto [_, diameter] = bfs(farthest_node, graph); // Second BFS to find the diameter
    return diameter;
}

int travelTime(int N, int M, int L, int A[], int B[], int T[]) {
    // Step 1: Build the adjacency list for the graph
    vector<vector<pair<int, int>>> graph(N);
    for (int i = 0; i < M; ++i) {
        graph[A[i]].emplace_back(B[i], T[i]);
        graph[B[i]].emplace_back(A[i], T[i]);
    }

    // Step 2: Find all connected components and their diameters
    vector<bool> visited(N, false);
    vector<int> component_diameters;
    vector<int> farthest_distances;

    for (int i = 0; i < N; ++i) {
        if (!visited[i]) {
            // Perform BFS to find all nodes in the same connected component
            vector<int> component_nodes;
            queue<int> q;
            q.push(i);
            visited[i] = true;

            while (!q.empty()) {
                int current = q.front();
                q.pop();
                component_nodes.push_back(current);

                for (const auto& neighbor : graph[current]) {
                    int next = neighbor.first;
                    if (!visited[next]) {
                        visited[next] = true;
                        q.push(next);
                    }
                }
            }

            // Calculate the diameter of the current connected component
            int component_diameter = calculateDiameter(graph, component_nodes[0]);
            component_diameters.push_back(component_diameter);

            // Find the farthest distance within this component
            auto [_, farthest_distance] = bfs(component_nodes[0], graph);
            farthest_distances.push_back(farthest_distance);
        }
    }

    // Step 3: Sort the farthest distances in descending order
    sort(farthest_distances.rbegin(), farthest_distances.rend());

    // Step 4: Calculate the maximum travel time after adding new trails
    int max_travel_time = 0;
    if (farthest_distances.size() == 1) {
        max_travel_time = farthest_distances[0]; // Only one component
    } else if (farthest_distances.size() == 2) {
        max_travel_time = max(farthest_distances[0] + L + farthest_distances[1], farthest_distances[0]);
    } else {
        max_travel_time = max({
            farthest_distances[0],
            farthest_distances[1] + L + farthest_distances[2]
        });
    }

    return max_travel_time;
}

