void encode_integer(int x, int max_bits) {
    for (int i = 0; i < max_bits; i++) {
        encode_bit((x >> i) & 1);
    }
}

// Function to decode a single integer from a sequence of bits
int decode_integer(int max_bits) {
    int x = 0;
    for (int i = 0; i < max_bits; i++) {
        x |= (decode_bit() << i);
    }
    return x;
}

