    vector<vector<int>> adj(N);
    for (int i = 0; i < P; ++i) {
        adj[A[i]].push_back(B[i]);
        adj[B[i]].push_back(A[i]);
    }

    // Precompute shortest distances from each hub using BFS
    vector<vector<int>> distances(H, vector<int>(N, -1)); // Distance from each hub to every city
    for (int hub = 0; hub < H; ++hub) {
        queue<int> q;
        q.push(hub);
        distances[hub][hub] = 0;

        while (!q.empty()) {
            int city = q.front();
            q.pop();
            for (int neighbor : adj[city]) {
                if (distances[hub][neighbor] == -1) {
                    distances[hub][neighbor] = distances[hub][city] + 1;
                    q.push(neighbor);
                }
            }
        }
    }

    // Encode distances into bits
    for (int hub = 0; hub < H; ++hub) {
        for (int city = 0; city < N; ++city) {
            int d = distances[hub][city];
            while (d > 0) {
                encode_bit(d & 1); // Encode the least significant bit
