int biggest_stadium(int N, vector<vector<int>> F) {
    // Precompute prefix sums for rows and columns
    vector<vector<int>> rowSum(N, vector<int>(N + 1, 0));
    vector<vector<int>> colSum(N + 1, vector<int>(N, 0));

    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            rowSum[r][c + 1] = rowSum[r][c] + F[r][c];
            colSum[r + 1][c] = colSum[r][c] + F[r][c];
        }
    }

    // Helper function to check if a range is empty in a row
    auto isRowClear = [&](int r, int c1, int c2) {
        return rowSum[r][c2 + 1] - rowSum[r][c1] == 0;
    };

    // Helper function to check if a range is empty in a column
    auto isColClear = [&](int c, int r1, int r2) {
        return colSum[r2 + 1][c] - colSum[r1][c] == 0;
    };

    int maxSize = 1; // Minimum possible size of a stadium

    // Binary search for maximum size
    int left = 1, right = N * N;
    while (left <= right) {
        int mid = (left + right) / 2;
        bool found = false;

        // Check all possible stadiums of size `mid`
        for (int r1 = 0; r1 < N && !found; ++r1) {
            for (int c1 = 0; c1 < N && !found; ++c1) {
                int r2 = r1 + (mid - 1) / N; // Bottom-right row
                int c2 = c1 + (mid - 1) % N; // Bottom-right column

                if (r2 >= N || c2 >= N) continue;

                // Check if all rows and columns in the stadium are clear
                bool valid = true;
                for (int r = r1; r <= r2; ++r) {
                    if (!isRowClear(r, c1, c2)) {
                        valid = false;
                        break;
                    }
                }
                for (int c = c1; c <= c2; ++c) {
                    if (!isColClear(c, r1, r2)) {
                        valid = false;
                        break;
                    }
                }

                if (valid) {
                    found = true;
                    break;
                }
            }
        }

        if (found) {
            maxSize = mid;
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }

    return maxSize;
}

