int biggest_stadium(int N, vector<vector<int>> F) {
    // Precompute row-wise prefix sums for empty cells
    vector<vector<int>> rowPrefix(N, vector<int>(N + 1, 0));
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            rowPrefix[r][c + 1] = rowPrefix[r][c] + (F[r][c] == 0 ? 1 : 0);
        }
    }

    // Precompute column-wise prefix sums for empty cells
    vector<vector<int>> colPrefix(N + 1, vector<int>(N, 0));
    for (int c = 0; c < N; ++c) {
        for (int r = 0; r < N; ++r) {
            colPrefix[r + 1][c] = colPrefix[r][c] + (F[r][c] == 0 ? 1 : 0);
        }
    }

    // Function to check if a subgrid of size s is valid
    auto is_valid = [&](int s) -> bool {
        for (int r = 0; r + s - 1 < N; ++r) {
            for (int c = 0; c + s - 1 < N; ++c) {
                // Check all rows in the subgrid
                bool valid = true;
                for (int i = r; i < r + s; ++i) {
                    if (rowPrefix[i][c + s] - rowPrefix[i][c] != s) {
                        valid = false;
                        break;
                    }
                }
                if (!valid) continue;

                // Check all columns in the subgrid
                for (int j = c; j < c + s; ++j) {
                    if (colPrefix[r + s][j] - colPrefix[r][j] != s) {
                        valid = false;
                        break;
                    }
                }
                if (valid) return true;
            }
        }
        return false;
    };

    // Binary search for the maximum size of the stadium
    int low = 1, high = N, best = 0;
    while (low <= high) {
        int mid = (low + high) / 2;
        if (is_valid(mid)) {
            best = mid;
            low = mid + 1;
        } else {
            high = mid - 1;
        }
    }

    return best * best;
}

