int biggest_stadium(int N, vector<vector<int>> F) {
    // Precompute the maximum consecutive zeros in rows and columns
    vector<vector<int>> row(N, vector<int>(N, 0));
    vector<vector<int>> col(N, vector<int>(N, 0));

    // Precompute row-wise consecutive zeros
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            if (F[r][c] == 0) {
                row[r][c] = (c == 0) ? 1 : row[r][c - 1] + 1;
            }
        }
    }

    // Precompute column-wise consecutive zeros
    for (int c = 0; c < N; ++c) {
        for (int r = 0; r < N; ++r) {
            if (F[r][c] == 0) {
                col[r][c] = (r == 0) ? 1 : col[r - 1][c] + 1;
            }
        }
    }

    // Variable to store the maximum size of a valid stadium
    int max_size = 0;

    // Check every possible stadium center
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            if (F[r][c] == 1) continue; // Skip trees

            // Minimum row and column span from this center
            int min_row_span = N, min_col_span = N;

            // Expand outward to form the largest possible stadium
            for (int d = 0; r - d >= 0 && r + d < N && c - d >= 0 && c + d < N; ++d) {
                // Update the minimum row and column span
                min_row_span = min(min_row_span, row[r + d][c + d] - (c - d - 1 >= 0 ? row[r + d][c - d - 1] : 0));
                min_col_span = min(min_col_span, col[r + d][c + d] - (r - d - 1 >= 0 ? col[r - d - 1][c + d] : 0));

                // Check if the current stadium is valid
                if (min_row_span >= 2 * d + 1 && min_col_span >= 2 * d + 1) {
                    max_size = max(max_size, (2 * d + 1) * (2 * d + 1));
                } else {
                    break; // No need to expand further
                }
            }
        }
    }

    return max_size;
}

