int biggest_stadium(int N, vector<vector<int>> F) {
    // Prefix sums for rows and columns
    vector<vector<int>> rowSum(N, vector<int>(N, 0));
    vector<vector<int>> colSum(N, vector<int>(N, 0));

    // Precompute row and column prefix sums
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            rowSum[r][c] = (F[r][c] == 0) + (c > 0 ? rowSum[r][c - 1] : 0);
            colSum[r][c] = (F[r][c] == 0) + (r > 0 ? colSum[r - 1][c] : 0);
        }
    }

    // DP table to store the largest stadium size ending at (r, c)
    vector<vector<int>> dp(N, vector<int>(N, 0));
    int maxStadiumSize = 0;

    // Compute DP values
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            if (F[r][c] == 1) {
                dp[r][c] = 0; // Cell is not empty
            } else {
                dp[r][c] = 1; // Single cell is always a valid stadium
                if (r > 0 && c > 0) {
                    // Check the minimum size of a rectangle that can extend to (r, c)
                    int minWidth = dp[r - 1][c];
                    int minHeight = dp[r][c - 1];
                    int width = minWidth + 1;
                    int height = minHeight + 1;

                    // Validate the rectangle using prefix sums
                    if (rowSum[r][c] - (c - width >= 0 ? rowSum[r][c - width] : 0) == width &&
                        colSum[r][c] - (r - height >= 0 ? colSum[r - height][c] : 0) == height) {
                        dp[r][c] = width * height;
                    }
                }
                maxStadiumSize = max(maxStadiumSize, dp[r][c]);
            }
        }
    }

    return maxStadiumSize;
}

