// Dijkstra's algorithm helper function
int dijkstra(int startCol, int endCol) {
    // Priority queue for Dijkstra's algorithm
    priority_queue<pair<int, pair<int, int>>, vector<pair<int, pair<int, int>>>, greater<>> pq;

    // Distance array
    vector<vector<int>> dist(R, vector<int>(C, INF));
    dist[0][startCol] = 0;

    // Push starting point into the priority queue
    pq.push({0, {0, startCol}});

    while (!pq.empty()) {
        auto [currentDist, currentPos] = pq.top();
        pq.pop();
        int row = currentPos.first;
        int col = currentPos.second;

        // If we've reached the desired destination
        if (row == R - 1 && col == endCol) {
            return currentDist;
        }

        // Skip if we already have a better distance
        if (currentDist > dist[row][col]) {
            continue;
        }

        // Explore neighbors
        // Move right on the same row
        if (col + 1 < C) {
            int newDist = currentDist + H[row][col];
            if (newDist < dist[row][col + 1]) {
                dist[row][col + 1] = newDist;
                pq.push({newDist, {row, col + 1}});
            }
        }
        // Move left on the same row
        if (col - 1 >= 0) {
            int newDist = currentDist + H[row][col - 1];
            if (newDist < dist[row][col - 1]) {
                dist[row][col - 1] = newDist;
                pq.push({newDist, {row, col - 1}});
            }
        }
        // Move down to the next row
        if (row + 1 < R) {
            int newDist = currentDist + V[row][col];
            if (newDist < dist[row + 1][col]) {
                dist[row + 1][col] = newDist;
                pq.push({newDist, {row + 1, col}});
            }
        }
    }

    return INF; // If no path is found (should not happen with valid input)
}

