// Escape function to compute the minimum wombats passed
int escape(int V1, int V2) {
    // Dijkstra's algorithm to find the shortest path
    vector<vector<int>> dist(R, vector<int>(C, numeric_limits<int>::max()));
    priority_queue<Node, vector<Node>, greater<Node>> pq;

    // Start from (0, V1)
    dist[0][V1] = 0;
    pq.push({0, V1, 0});

    while (!pq.empty()) {
        Node current = pq.top();
        pq.pop();

        int row = current.row;
        int col = current.col;
        int cost = current.cost;

        // If we reach the destination (R-1, V2), return the cost
        if (row == R - 1 && col == V2) {
            return cost;
        }

        // Skip if this is not the shortest path to this node
        if (cost > dist[row][col]) {
            continue;
        }

        // Move left on the same horizontal road
        if (col > 0) {
            int newCost = cost + horizontal[row][col - 1];
            if (newCost < dist[row][col - 1]) {
                dist[row][col - 1] = newCost;
                pq.push({row, col - 1, newCost});
            }
        }

        // Move right on the same horizontal road
        if (col < C - 1) {
            int newCost = cost + horizontal[row][col];
            if (newCost < dist[row][col + 1]) {
                dist[row][col + 1] = newCost;
                pq.push({row, col + 1, newCost});
            }
        }

        // Move down on the vertical road
        if (row < R - 1) {
            int newCost = cost + vertical[row][col];
            if (newCost < dist[row + 1][col]) {
                dist[row + 1][col] = newCost;
                pq.push({row + 1, col, newCost});
            }
        }
    }

    // If no path is found (shouldn't happen under valid input), return a large value
    return numeric_limits<int>::max();
}

