// Function to convert 2D grid coordinates to 1D node indices
inline int nodeIndex(int row, int col) {
    return row * C + col;
}

// Initialize the graph
void buildGraph() {
    graph.assign(R * C, vector<Edge>());

    for (int p = 0; p < R; ++p) {
        for (int q = 0; q < C; ++q) {
            // Horizontal edges
            if (q < C - 1) {
                graph[nodeIndex(p, q)].push_back({nodeIndex(p, q + 1), H[p][q]});
                graph[nodeIndex(p, q + 1)].push_back({nodeIndex(p, q), H[p][q]});
            }
            // Vertical edges
            if (p < R - 1) {
                graph[nodeIndex(p, q)].push_back({nodeIndex(p + 1, q), V[p][q]});
            }
        }
    }
}

// Dijkstra's algorithm to find the shortest path
int dijkstra(int start, int end) {
    vector<int> dist(R * C, INF);
    priority_queue<pair<int, int>, vector<pair<int, int>>, greater<>> pq;

    dist[start] = 0;
    pq.push({0, start});

    while (!pq.empty()) {
        auto [currentDist, u] = pq.top();
        pq.pop();

        if (currentDist > dist[u]) continue;

        for (const auto &edge : graph[u]) {
            int v = edge.to;
            int weight = edge.weight;

            if (dist[u] + weight < dist[v]) {
                dist[v] = dist[u] + weight;
                pq.push({dist[v], v});
            }
        }
    }

    return dist[end];
}

