    // Update the graph
    int u = nodeIndex(P, Q);
    int v = nodeIndex(P, Q + 1);

    for (auto &edge : graph[u]) {
        if (edge.to == v) {
            edge.weight = W;
            break;
        }
    }

    for (auto &edge : graph[v]) {
        if (edge.to == u) {
