// Helper function to calculate the minimum number of swaps to transform one permutation into another
int calculate_min_swaps(vector<int>& arr1, vector<int>& arr2) {
    int n = arr1.size();
    vector<bool> visited(n, false);
    vector<pair<int, int>> sorted_arr1(n);
    for (int i = 0; i < n; ++i) {
        sorted_arr1[i] = {arr1[i], i};
    }
    sort(sorted_arr1.begin(), sorted_arr1.end());

    vector<int> target_index(n);
    for (int i = 0; i < n; ++i) {
        target_index[sorted_arr1[i].second] = i;
    }

    int swaps = 0;
    for (int i = 0; i < n; ++i) {
        if (visited[i] || target_index[i] == i) {
            continue;
        }

        int cycle_size = 0;
        int j = i;
        while (!visited[j]) {
            visited[j] = true;
            j = target_index[j];
            ++cycle_size;
        }
        swaps += cycle_size - 1;
    }
    return swaps;
}

