// Function to get the mutation distance between substrings a[x..y] and b[x..y]
int get_distance(int x, int y) {
    // Get character counts for substrings a[x..y] and b[x..y]
    vector<int> count_a(3), count_b(3);
    for (int i = 0; i < 3; ++i) {
        count_a[i] = prefix_count_a[i][y + 1] - prefix_count_a[i][x];
        count_b[i] = prefix_count_b[i][y + 1] - prefix_count_b[i][x];
    }

    // If character counts differ, transformation is impossible
    if (count_a != count_b) {
        return -1;
    }

    // Extract substrings
    string sub_a = global_a.substr(x, y - x + 1);
    string sub_b = global_b.substr(x, y - x + 1);

    // Map characters to their positions in substring b
    unordered_map<char, vector<int>> pos_map_b;
    for (int i = 0; i < sub_b.size(); ++i) {
        pos_map_b[sub_b[i]].push_back(i);
    }

    // Create the permutation array for transforming sub_a into sub_b
    vector<int> permutation(sub_a.size());
    unordered_map<char, int> pos_index;
    for (int i = 0; i < sub_a.size(); ++i) {
        char c = sub_a[i];
        permutation[i] = pos_map_b[c][pos_index[c]++];
    }

    // Count the number of cycles in the permutation
    vector<bool> visited(permutation.size(), false);
    int cycles = 0;
    for (int i = 0; i < permutation.size(); ++i) {
        if (!visited[i]) {
            cycles++;
            int j = i;
            while (!visited[j]) {
                visited[j] = true;
                j = permutation[j];
            }
        }
    }

    // Minimum number of swaps is the length of the array minus the number of cycles
    return sub_a.size() - cycles;
}

